--
-- Triggers `article`
--
DELIMITER $$
CREATE TRIGGER `set_profileid_on_article_insert` BEFORE INSERT ON `article` FOR EACH ROW BEGIN
    DECLARE profile_id INT;
    
    -- Get the ProfileID from creator_profiles based on AdminId
    SELECT ProfileID INTO profile_id 
    FROM creator_profiles 
    WHERE AdminId = NEW.AdminId AND isDeleted = 'notDeleted' 
    LIMIT 1;
    
    -- Set the ProfileID if found
    IF profile_id IS NOT NULL THEN
        SET NEW.ProfileID = profile_id;
    END IF;
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `update_creator_stats_on_article_publish` AFTER INSERT ON `article` FOR EACH ROW BEGIN
            DECLARE creator_profile_id INT;
            SELECT ProfileID INTO creator_profile_id 
            FROM creator_profiles 
            WHERE AdminId = NEW.AdminId AND isDeleted = 'notDeleted';
            
            IF creator_profile_id IS NOT NULL THEN
                INSERT INTO creator_statistics (ProfileID, Date, ArticlesPublished, TotalViews)
                VALUES (creator_profile_id, CURDATE(), 1, 0)
                ON DUPLICATE KEY UPDATE
                ArticlesPublished = ArticlesPublished + 1;
                
                UPDATE creator_profiles 
                SET TotalArticles = TotalArticles + 1
                WHERE ProfileID = creator_profile_id;
            END IF;
        END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `update_profileid_on_adminid_change` BEFORE UPDATE ON `article` FOR EACH ROW BEGIN
    DECLARE profile_id INT;
    
    -- Only update if AdminId changed
    IF OLD.AdminId != NEW.AdminId THEN
        -- Get the new ProfileID from creator_profiles based on new AdminId
        SELECT ProfileID INTO profile_id 
        FROM creator_profiles 
        WHERE AdminId = NEW.AdminId AND isDeleted = 'notDeleted' 
        LIMIT 1;
        
        -- Set the ProfileID if found
        IF profile_id IS NOT NULL THEN
            SET NEW.ProfileID = profile_id;
        ELSE
            SET NEW.ProfileID = NULL;
        END IF;
    END IF;
END
$$
DELIMITER ;

