--
-- Table structure for table `creator_followers`
--

CREATE TABLE `creator_followers` (
  `FollowID` int(11) NOT NULL,
  `FollowerID` int(11) NOT NULL,
  `FollowingID` int(11) NOT NULL,
  `FollowDate` timestamp NOT NULL DEFAULT current_timestamp(),
  `Status` enum('active','blocked') DEFAULT 'active'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

--
-- Triggers `creator_followers`
--
DELIMITER $$
CREATE TRIGGER `update_follower_counts_on_follow` AFTER INSERT ON `creator_followers` FOR EACH ROW BEGIN
            IF NEW.Status = 'active' THEN
                UPDATE creator_profiles 
                SET FollowingCount = FollowingCount + 1
                WHERE ProfileID = NEW.FollowerID;
                
                UPDATE creator_profiles 
                SET FollowersCount = FollowersCount + 1
                WHERE ProfileID = NEW.FollowingID;
            END IF;
        END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `update_follower_counts_on_unfollow` AFTER UPDATE ON `creator_followers` FOR EACH ROW BEGIN
            IF OLD.Status = 'active' AND NEW.Status != 'active' THEN
                UPDATE creator_profiles 
                SET FollowingCount = FollowingCount - 1
                WHERE ProfileID = NEW.FollowerID;
                
                UPDATE creator_profiles 
                SET FollowersCount = FollowersCount - 1
                WHERE ProfileID = NEW.FollowingID;
            END IF;
        END
$$
DELIMITER ;

