--
-- Structure for view `creator_articles_summary`
--
DROP TABLE IF EXISTS `creator_articles_summary`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `creator_articles_summary`  AS SELECT `cp`.`ProfileID` AS `ProfileID`, `cp`.`Username` AS `Username`, `cp`.`DisplayName` AS `DisplayName`, `cp`.`ProfilePhoto` AS `ProfilePhoto`, `cp`.`IsVerified` AS `IsVerified`, `cp`.`IsFeatured` AS `IsFeatured`, count(`a`.`ArticleID`) AS `TotalArticles`, sum(coalesce(`a`.`Engagement_score`,0)) AS `TotalEngagement`, max(`a`.`Date`) AS `LastArticleDate` FROM (`creator_profiles` `cp` left join `article` `a` on(`cp`.`AdminId` = `a`.`AdminId` and `a`.`Published` = 'published')) WHERE `cp`.`isDeleted` = 'notDeleted' GROUP BY `cp`.`ProfileID` ;

